import React, { useEffect, useState } from 'react';
import { getAllCartItems, deleteCartItem, updateCart } from '~/apiServices/cartService';

const Cart = () => {
    const [cartItems, setCartItems] = useState([]);

    // Lấy tất cả các sản phẩm trong giỏ hàng
    useEffect(() => {
        const fetchCartItems = async () => {
            const items = await getAllCartItems();
            setCartItems(items);
        };
        fetchCartItems();
    }, []);

    // Cập nhật số lượng của sản phẩm trong giỏ hàng
    const handleUpdateQuantity = async (productId, newQuantity) => {
        const updatedCart = await updateCart(productId, newQuantity);
        setCartItems(updatedCart);
    };

    // Xóa sản phẩm khỏi giỏ hàng
    const handleDeleteCartItem = async (cartItemId) => {
        const updatedCart = await deleteCartItem(cartItemId);
        setCartItems(updatedCart);
    };

    return (
        <div>
            <h2>Your Cart</h2>
            {cartItems.length === 0 ? (
                <p>Your cart is empty.</p>
            ) : (
                <ul>
                    {cartItems.map((item) => (
                        <li key={item.id}>
                            <h3>{item.productName}</h3>
                            <p>Price: {item.price}</p>
                            <p>Quantity: 
                                <input 
                                    type="number" 
                                    value={item.quantity} 
                                    onChange={(e) => handleUpdateQuantity(item.productId, e.target.value)} 
                                />
                            </p>
                            <button onClick={() => handleDeleteCartItem(item.id)}>Remove</button>
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default Cart;
