import React, { useEffect, useState } from 'react';
import { getAllCartItems, deleteCartItem, updateCart } from '~/apiServices/cartService';
import classNames from 'classnames/bind';
import styles from './Cart.module.scss'; // Import CSS module riêng cho Cart

const cx = classNames.bind(styles);

const Cart = () => {
    const [cartItems, setCartItems] = useState([]);

    // Lấy tất cả các sản phẩm trong giỏ hàng
    useEffect(() => {
        const fetchCartItems = async () => {
            const items = await getAllCartItems();
            setCartItems(items);
        };
        fetchCartItems();
    }, []);

    // Cập nhật số lượng của sản phẩm trong giỏ hàng
    const handleUpdateQuantity = async (productId, newQuantity) => {
        const updatedCart = await updateCart(productId, newQuantity);
        setCartItems(updatedCart);
    };

    // Xóa sản phẩm khỏi giỏ hàng
    const handleDeleteCartItem = async (cartItemId) => {
        const updatedCart = await deleteCartItem(cartItemId);
        setCartItems(updatedCart);
    };

    return (
        <div className={cx('wrapper')}>
            <h2>Your Cart</h2>
            {cartItems.length === 0 ? (
                <p>Your cart is empty.</p>
            ) : (
                <ul className={cx('cart-list')}>
                    {cartItems.map((item) => (
                        <li key={item.id} className={cx('cart-item')}>
                            <div className={cx('image')}>
                                <img src={item.productImageUrl} alt={item.productName} />
                            </div>
                            <div className={cx('details')}>
                                <h3 className={cx('product-name')}>{item.productName}</h3>
                                <p className={cx('price')}>Price: {item.price}</p>
                                <p className={cx('quantity')}>
                                    Quantity:
                                    <input
                                        type="number"
                                        value={item.quantity}
                                        onChange={(e) => handleUpdateQuantity(item.productId, e.target.value)}
                                    />
                                </p>
                                <button className={cx('remove-btn')} onClick={() => handleDeleteCartItem(item.id)}>
                                    Remove
                                </button>
                            </div>
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default Cart;
