import { useEffect, useState } from 'react';
import classNames from 'classnames/bind';
import ProductItem from '../ProductItem';
import styles from './Product.module.scss';
import * as productList from '~/apiServices/productList';
import Image from '../Image';
import { Card, Button } from 'react-bootstrap';

const cx = classNames.bind(styles);

function Product() {
    const [products, setProducts] = useState([]);
    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const result = await productList.getProducts(1);
                setProducts(result);
            } catch (error) {
                console.error('Failed to fetch products: ', error);
            }
        };
        fetchProducts();
    }, []);

    return (
        <div className={cx('product-list')}>
            {products.map((product) => (
                <div className={cx('card')} style={{ width: '180px' }}>
                    <img
                        className={cx('card-img-top')}
                        src="https://giaydino.com/wp-content/uploads/2023/07/Giay-Nike-Air-Jordan-1-High-OG-Denim.jpg"
                        alt="Card image"
                    />
                    <div className="card-body">
                        <h4 className="card-title">{product.name}</h4>
                        <p className="card-text">{product.description}</p>
                        <button className="btn btn-primary">Buy</button>
                    </div>
                </div>
            ))}
        </div>
    );
}

export default Product;
