import { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import classNames from 'classnames/bind';
import styles from './ProductDetails.module.scss';
import * as productDetail from '~/apiServices/productDetail';
import * as cartService from '~/apiServices/cartService';

const cx = classNames.bind(styles);

function ProductDetails({ cartId, onAddToCart }) { // Nhận props từ component cha
    const { productId } = useParams();
    const [product, setProduct] = useState(null); 

    useEffect(() => {
        const fetchProductDetail = async () => {
            try {
                const result = await productDetail.getProductDetail(productId);
                setProduct(result); 
            } catch (error) {
                console.error('Failed to fetch product details: ', error);
            }
        };
        fetchProductDetail();
    }, [productId]);

    const handleAddToCart = async () => {
        try {
            if (!product) {
                throw new Error('Product not found');
            }
            const result = await cartService.addToCart(cartId, productId, quantity, product.price); // Sử dụng cartId và quantity = 1
            if (result) {
                alert(`${product.name} has been added to the cart!`);
                onAddToCart(); // Gọi callback để cập nhật giỏ hàng
            }
        } catch (error) {
            console.error('Error adding to cart: ', error);
        }
    };

    if (!product) {
        return <p>Loading...</p>; 
    }

    return (
        <div className={cx('wrapper')}>
            <div className={cx('image')}>
                <img src={product.imageUrl} className="rounded" alt={product.name} />
            </div>
            <div className={cx('container')}>
                <h2 className={cx('name')}>{product.name}</h2>
                <p className={cx('description')}>{product.description}</p>
                <p className={cx('price')}>Price: {product.price}</p>
                <p className={cx('quantity')}>Quantity: {product.quantity}</p>

                <button className={cx('btn-buy')}>Buy</button>
                <button className={cx('btn-cart')} onClick={handleAddToCart}> Add to cart</button>
            </div>
        </div>
    );
}

export default ProductDetails;
