import { useEffect, useState } from 'react';
import classNames from 'classnames/bind';
import styles from './Product.module.scss';
import * as productList from '~/apiServices/productList';
import { Link } from 'react-router-dom';

const cx = classNames.bind(styles);

function Product({ categoryId }) {
    const [products, setProducts] = useState([]);
    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const result = await productList.getProducts(categoryId);
                setProducts(result);
            } catch (error) {
                console.error('Failed to fetch products: ', error);
            }
        };
        fetchProducts();
    }, []);

    return (
        <div className={cx('product-list')}>
            {products.map((product) => (
                <Link to={`/@${product.productId}`}>
                    <div className={cx('card')} style={{ width: '180px' }}>
                        <img className={cx('card-img-top')} src={`${product.imageUrl}`} alt="Card image" />
                        <div className={cx('card-body')}>
                            <h4 className={cx('card-title')}>{product.name}</h4>
                            <p className={cx('card-text')}>{product.description}</p>
                        </div>
                    </div>
                </Link>
            ))}
        </div>
    );
}

export default Product;
