import { useEffect, useState } from 'react';
import classNames from 'classnames/bind';
import ProductItem from '../ProductItem';
import styles from './Product.module.scss';
import * as productList from '~/apiServices/productList';
import Image from '../Image';

const cx = classNames.bind(styles);

function Product() {
    const [products, setProducts] = useState([]);
    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const result = await productList.getProducts(1);
                setProducts(result);
            } catch (error) {
                console.error('Failed to fetch products: ', error);
            }
        };
        fetchProducts();
    }, []);

    return (
        <div className={cx('product-list')}>
            {products.map((product) => (
                <ProductItem key={product.id} data={product}>
                    <img
                        className={cx('product-img')}
                        src="https://www.google.com/url?sa=i&url=https%3A%2F%2Fgiaynhatchinhhang.vn%2Fnike-air-jordan-1-low-flyease-dm1206-066&psig=AOvVaw3HPzIGlMUYY2VqNh4qSb5G&ust=1726939425268000&source=images&cd=vfe&opi=89978449&ved=0CBQQjRxqFwoTCID4j-qE0ogDFQAAAAAdAAAAABAE"
                    />
                    <p>{product.price}</p>
                    <span>{product.quantity}</span>
                </ProductItem>
            ))}
        </div>
    );
}

export default Product;
