import React, { useEffect, useState } from 'react';
import { getCartItemsByCartId, getCartTotal, deleteCartItem, updateCart, addToCart } from '~/apiServices/cartService';
import classNames from 'classnames/bind';
import styles from './Cart.module.scss'; // Import CSS module riêng cho Cart

const cx = classNames.bind(styles);

const Cart = ({ cartId }) => {
    const [cartItems, setCartItems] = useState([]);
    const [cartTotal, setCartTotal] = useState(0);
    const [loading, setLoading] = useState(true); // Trạng thái tải

    // Lấy tất cả các sản phẩm trong giỏ hàng bằng cartId
    useEffect(() => {
        const fetchCartItems = async () => {
            setLoading(true); // Bắt đầu tải
            try {
                const items = await getCartItemsByCartId(cartId);
                setCartItems(items || []); // Nếu không có items, set về mảng rỗng
            } catch (error) {
                console.error('Failed to fetch cart items:', error);
            } finally {
                setLoading(false); // Kết thúc tải
            }
        };

        const fetchCartTotal = async () => {
            try {
                const total = await getCartTotal(cartId);
                setCartTotal(total);
            } catch (error) {
                console.error('Failed to fetch cart total:', error);
            }
        };

        fetchCartItems();
        fetchCartTotal();
    }, [cartId]);

    // Thêm sản phẩm vào giỏ hàng
    const handleAddToCart = async (productId, quantity, price) => {
        try {
            await addToCart(cartId, productId, quantity);
            // Cập nhật lại danh sách sản phẩm trong giỏ hàng sau khi thêm
            const updatedItems = await getCartItemsByCartId(cartId);
            setCartItems(updatedItems);
        } catch (error) {
            console.error('Failed to add item to cart:', error);
        }
    };

    // Cập nhật số lượng của sản phẩm trong giỏ hàng
    const handleUpdateQuantity = async (productId, newQuantity) => {
        try {
            await updateCart(productId, newQuantity);
            const updatedItems = await getCartItemsByCartId(cartId);
            setCartItems(updatedItems);
        } catch (error) {
            console.error('Failed to update cart:', error);
        }
    };

    // Xóa sản phẩm khỏi giỏ hàng
    const handleDeleteCartItem = async (cartItemId) => {
        try {
            await deleteCartItem(cartItemId);
            const updatedItems = await getCartItemsByCartId(cartId);
            setCartItems(updatedItems);
        } catch (error) {
            console.error('Failed to delete cart item:', error);
        }
    };

    if (loading) {
        return <p>Loading...</p>; // Hiển thị thông báo khi đang tải
    }

    return (
        <div className={cx('wrapper')}>
            <h2>Your Cart</h2>
            {cartItems.length === 0 ? (
                <p>Your cart is empty.</p>
            ) : (
                <ul className={cx('cart-list')}>
                    {cartItems.map((item) => (
                        <li key={item.id} className={cx('cart-item')}>
                            <div className={cx('image')}>
                                <img src={item.productImageUrl} alt={item.productName} />
                            </div>
                            <div className={cx('details')}>
                                <h3 className={cx('product-name')}>{item.productName}</h3>
                                <p className={cx('price')}>Price: {item.price}</p>
                                <p className={cx('quantity')}>
                                    Quantity:
                                    <input
                                        type="number"
                                        value={item.quantity}
                                        onChange={(e) => handleUpdateQuantity(item.productId, e.target.value)}
                                    />
                                </p>
                                <button className={cx('remove-btn')} onClick={() => handleDeleteCartItem(item.id)}>
                                    Remove
                                </button>
                            </div>
                        </li>
                    ))}
                </ul>
            )}
            <h3>Total: {cartTotal}</h3> {/* Hiển thị tổng giá trị của giỏ hàng */}
        </div>
    );
};

export default Cart;
