import { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom'; // Để lấy categoryId từ URL
import classNames from 'classnames/bind';
import styles from './Product.module.scss';
import * as productList from '~/apiServices/productList';
import { Link } from 'react-router-dom';
import * as productByCategoryId from '~/apiServices/productByCategoryId';

const cx = classNames.bind(styles);

function Product() {
    const { categoryId } = useParams(); // Lấy categoryId từ URL (nếu có)
    const [products, setProducts] = useState([]);

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                let result;
                if (categoryId) {
                    // Gọi API lấy sản phẩm theo categoryId nếu có
                    result = await productByCategoryId.getProductsByCategoryId(categoryId);
                } else {
                    // Gọi API lấy tất cả sản phẩm nếu không có categoryId
                    result = await productList.getProducts();
                }
                setProducts(result);
            } catch (error) {
                console.error('Failed to fetch products: ', error);
            }
        };
        fetchProducts();
    }, [categoryId]); // Chạy lại khi categoryId thay đổi

    return (
        <div className={cx('product-list')}>
            {products.length === 0 ? (
                // Hiển thị thông báo nếu không có sản phẩm
                <h2>Không có sản phẩm</h2>
            ) : (
                products.map((product) => (
                    <Link to={`/@${product.productId}`} key={product.productId}>
                        <div className={cx('card')} style={{ width: '180px' }}>
                            <img className={cx('card-img-top')} src={product.imageUrl} alt={product.name} />
                            <div className={cx('card-body')}>
                                <h4 className={cx('card-title')}>{product.name}</h4>
                                <p className={cx('card-text')}>{product.description}</p>
                            </div>
                        </div>
                    </Link>
                ))
            )}
        </div>
    );
}

export default Product;
