import { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import classNames from 'classnames/bind';
import styles from './ProductDetail.module.scss';
import * as productDetail from '~/apiServices/productDetail';

const cx = classNames.bind(styles);

function ProductDetails() {
    const { productId } = useParams();
    const [product, setProduct] = useState('');

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const result = await productDetail.getProductDetail(productId);
                setProduct(result);
            } catch (error) {
                console.error('Failed to fetch products: ', error);
            }
        };
        fetchProducts();
    }, [productId]);

    return (
        <div className={cx('product-list')}>
            {product.map((p) => (
                <div className={cx('card')} style={{ width: '180px' }}>
                    <img className={cx('card-img-top')} src={p.imageUrl} alt={p.name} />
                    <div className={cx('card-body')}>
                        <h4 className={cx('card-title')}>{p.name}</h4>
                        <p className={cx('card-text')}>{p.description}</p>
                    </div>
                </div>
            ))}
        </div>
    );
}

export default ProductDetails;
