import * as request from '~/utils/request';

// Lấy tất cả sản phẩm trong giỏ hàng
export const getAllCartItems = async () => {
    try {
        const res = await request.get('/Cart/get-all');
        return res.data;
    } catch (error) {
        console.error('Failed to fetch cart items: ', error);
    }
};
// Thêm sản phẩm vào giỏ hàng
export const addToCart = async (productId, quantity) => {
    try {
        const res = await request.post('/Cart/add-to-cart', {
            productId,
            quantity,
        });
        return res; // Trả về dữ liệu mà không cần kiểm tra status
    } catch (error) {
        console.error('Failed to add product to cart:', error);
        return null;
    }
};
// Cập nhật giỏ hàng
export const updateCart = async (productId, quantity) => {
    try {
        const res = await request.put('/Cart/update-cart', {
            productId,
            quantity,
        });
        return res.data;
    } catch (error) {
        console.error('Failed to update cart: ', error);
    }
};
// Xóa sản phẩm khỏi giỏ hàng
export const deleteCartItem = async (cartItemId) => {
    try {
        const res = await request.delete(`/Cart/delete-cart-item/${cartItemId}`);
        return res.data;
    } catch (error) {
        console.error('Failed to delete cart item: ', error);
    }
};
